import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

const Results: React.FC = () => {
  const location = useLocation();
  const params = new URLSearchParams(location.search); // Get query parameters
  const url = params.get('url');

  const [performanceData, setPerformanceData] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [suggestions, setSuggestions] = useState<string | null>(null);

  useEffect(() => {
    const fetchPerformanceData = async () => {
      if (!url) return;

      try {
        setLoading(true);

        // Fetching performance data from Google PageSpeed Insights API
        const { data } = await axios.get(
          `https://www.googleapis.com/pagespeedonline/v5/runPagespeed?url=${url}&key=AIzaSyAIlzcp0uUXHP-jkRBH3npJ67uSPfSGzYU`
        );
        setPerformanceData(data);

        // Fetching suggestions from OpenAI API
        const openAiResponse = await axios.post(
          'https://api.openai.com/v1/completions',
          {
            model: 'text-davinci-003',
            prompt: `Based on the following performance data, provide suggestions for improvement: ${JSON.stringify(data)}`,
            max_tokens: 100,
          },
          {
            headers: {
              Authorization: `Bearer sk-2dkHENXgfAfUSUp6UggnuXvBIYOA9e37fQdXGDlY2BT3BlbkFJwv29tN6lz3UBNfm0TBzvWGG0Awbk4AC1SfT91VPD0A`, // Replace with your OpenAI API key
            },
          }
        );

        setSuggestions(openAiResponse.data.choices[0].text);
      } catch (err) {
        setError('Error fetching performance data or AI suggestions.');
      } finally {
        setLoading(false);
      }
    };

    fetchPerformanceData();
  }, [url]);

  if (loading) return <div className="text-center">Loading...</div>;
  if (error) return <div className="text-center text-red-500">{error}</div>;

  return (
    <div className="p-8">
      <h1 className="text-xl font-bold mb-4">Performance Results for {url}</h1>
      {performanceData && (
        <div className="mb-4">
          <h2 className="text-lg font-semibold">Performance Score: {(performanceData.lighthouseResult.categories.performance.score * 100).toFixed(2)}%</h2>
          <p className="mt-2">First Contentful Paint: {performanceData.lighthouseResult.metrics.FCP.displayValue}</p>
          <p>Speed Index: {performanceData.lighthouseResult.metrics.SI.displayValue}</p>
          <p>Total Blocking Time: {performanceData.lighthouseResult.metrics.TBT.displayValue}</p>
          <p>Largest Contentful Paint: {performanceData.lighthouseResult.metrics.LCP.displayValue}</p>
          <p>Cumulative Layout Shift: {performanceData.lighthouseResult.metrics.CLS.displayValue}</p>
        </div>
      )}
      {suggestions && (
        <div className="mt-4 p-4 border border-gray-300 rounded-lg">
          <h2 className="text-lg font-semibold">Suggestions for Improvement:</h2>
          <p>{suggestions}</p>
        </div>
      )}
    </div>
  );
};

export default Results;
